let products = [
    {
        ID: 1,
        Name: 'Coffie',
        Description: 'Tasty coffie',
        Price: 12,
    },
    {
        ID: 2,
        Name: 'Chocolate',
        Description: 'Tasty Chocolate',
        Price: 22,
    },
    {
        ID: 3,
        Name: 'Sugar',
        Description: 'Tasty Sugar',
        Price: 3,
    }
];


export const getProductsList = () => {
    return {
        type: 'GET_PRODUCTS_LIST',
        payload: products
    }
}

export const setProductSelected = (product) => {
    return {
        type: 'SET_PRODUCT_SELECTED',
        payload: product
    }
}

