import React, { Component } from 'react';

class ProductDetails extends Component {
    constructor(props) {
        super(props)
        this.state = {
            id: '',
            name: '',
            description: '',
            price: '',
        }
    }

    componentWillMount() {
        this.props.getProductsList();
    }

    componentWillReceiveProps(nextProps, prevProps) {
        if (nextProps.general.productSelected && nextProps.general.productSelected.ID) {
            this.setState({
                id: nextProps.general.productSelected.ID,
                name: nextProps.general.productSelected.Name,
                description: nextProps.general.productSelected.Description,
                price: nextProps.general.productSelected.Price,
            })
        }
    }

    _onChange(inputType, e) {
        this.setState({ [inputType]: e.target.value })
    }

    _save(){
        
    }

    render() {
        let { product } = this.props;

        return (
            <div className='productDetails'>
                <div><span>Product Details</span></div>
                <div className='productImg'></div>
                <div className='inputs'>
                    <label>
                        Name:<input type="text" value={this.state.name} onChange={(e) => this._onChange('name', e)}></input>
                    </label>
                    <label>
                        Description:<input type="text" value={this.state.description} onChange={(e) => this._onChange('description', e)}></input>
                    </label>
                    <label>
                        Price:<input type="text" value={this.state.price} onChange={(e) => this._onChange('price', e)}></input>
                    </label>
                </div>
                <div className='saveeBtn'><button onClick={()=>this._save()}>save</button></div>
            </div>
        );
    }
}

export default (ProductDetails);
